public class Part3
{
  public int findStopCodon(String dna, int startIndex, String stopCodon){
      int currIndex = dna.indexOf(stopCodon,startIndex +3);
     
      while(currIndex != -1)
      {
           int diff = currIndex - startIndex;
           if(diff % 3 == 0){
               return currIndex;
           }
           else {
               currIndex = dna.indexOf(stopCodon, currIndex + 1);
           }
      }
      return -1;
  }
  
  public String findGene(String dna){
      int startCodon = dna.indexOf("ATG");
      if(startCodon == -1) return "";
      int taaIndex = findStopCodon(dna,startCodon,"TAA");
      int tagIndex = findStopCodon(dna,startCodon,"TAG");
      int tgaIndex = findStopCodon(dna,startCodon,"TGA");
      
      int minIndex = -1;
      if (taaIndex == -1 || (tagIndex != -1 && tagIndex < taaIndex))
          minIndex = tagIndex;
      else
          minIndex = taaIndex;
      
      if (minIndex == -1 || (tgaIndex != -1 && tgaIndex < minIndex))
          minIndex = tgaIndex;
      
      if(minIndex == -1)          
      return "";
      
      
      return dna.substring(startCodon,minIndex + 3);
  }
  
  public void printAllGenes(String dna){
    int startIndex = 0;
    
    while (true) {
        String gene = findGene(dna.substring(startIndex));
        if (gene.equals("")) {
            break; // plus de gènes
        }
        System.out.println("gene: " + gene);
        
        // avancer l’index après le gène trouvé
        startIndex = dna.indexOf(gene, startIndex) + gene.length();
    }
}
 
  public int countGenes(String dna){
      int startIndex = 0;
      int count = 0;
      while(true){
          String gene = findGene(dna.substring(startIndex));
          if(gene.equals("")) break;
          count++;
          startIndex= dna.indexOf(gene,startIndex) + gene.length();
      }
      
      return count;
  }
  
  public void testFileCountGenes(){
       ur = new URLResource("https://www.dukelearntoprogram.com/course2/data/GRch38dnapart.fa");
      String content = ur.asString();
      System.out.println("nombre de gene: "+countGenes(content));
      //System.out.println("nombre de gene: "+content);
      
  }
  
  public void testAllGenes() {
      String dna = "";
      printAllGenes(dna);
  }
  public void testCountGenes () {
      String dna = "";
      System.out.println("nombre de gene: "+countGenes(dna));
  }
}